import { Component, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-add-post',
  templateUrl: './add-post.component.html',
  styleUrls: ['./add-post.component.scss']
})
export class AddPostComponent {

  @Output() onUser = new EventEmitter();

  nikInputValue = '';
  statusInputValue = '';
  linkInputValue = '';

  onName(event: any) {
    this.nikInputValue = event.target.value;
  }

  onStatus(event: any) {
    this.statusInputValue = event.target.value;
  }
  onLink(event: any) {
    this.linkInputValue = event.target.value;
  }

  addNewUser(nikValue: string, statusValue: string, linkValue: string,) {
    const value = {
      nik: nikValue,
      status: statusValue,
      link: linkValue
    }
    this.onUser.emit(value)
  }
}
