import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.scss'],
})
export class PostComponent {
  @Input() name!: string;
  @Input() status!: string;
  @Input() link!: string;
}
<div class="body">
    <img class="picture" [src]="link" alt="">
    <h2 class="name">{{name}}</h2>
    <span class="status">{{status}}</span>
    <button {removeUser} class="remove-btn" type="button">Remove User</button>
</div>
